import { Button, Grid, TextField } from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import './Login.css'
import {
    logInRequest
} from './LoginReducer'

/**
 * Component to render Action Station Lists and filters view
 * @returns {JSX.Element}
 * @constructor
 */
const Login = ({logIn }) => {

    const [userName,setUserName] = useState(null)
    const [password,setPassword] = useState(null)

    const logInActionHandler = () => {
        let useDetails = {
            userName:userName,
            password:password
        }
        logIn()
    }

    return (
        <>
            <Grid item xs={12} sm={12} md={12} lg={12} xl={12} className="login-page-main-grid">
                <Grid container direction="column" justifyContent="center" alignItems="center">
                    <Grid>
                        <TextField 
                            label="Username"
                            value={userName}
                            onChange={(e)=>{
                                setUserName(e.target.value)
                            }}
                        />
                    </Grid>
                    <Grid>
                        <TextField 
                            type="password"
                            label="Password"
                            value={password}
                            onChange={(e)=>{
                                setPassword(e.target.value)
                            }}
                        />
                    </Grid>
                    <Grid className='login-btn'>
                        <Button color='primary' variant='outlined' onClick={()=>{
                            logInActionHandler()
                        }}>
                            Login
                        </Button>
                    </Grid>
                </Grid>
            </Grid>
        </>
    );
};
const mapStateToProps = (state) => {
    return {

    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        logIn: async (data) => {
            return dispatch(await logInRequest(data))
        },
    }
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Login);
