import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import './Cart.css'
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Grid, Typography } from '@material-ui/core';


/**
 * Component to cart view
 * @returns {JSX.Element}
 * @constructor
 */
const Cart = ({ foodItems, open, closeDialogBox }) => {


    return (
        <div>
            <Dialog
                open={open}
                onClose={closeDialogBox}
                fullWidth="md"
            >
                <DialogTitle id="alert-dialog-title">Order Summary</DialogTitle>
                <DialogContent>
                    {
                        foodItems && foodItems.map((item)=>{
                            console.log("item",item)
                            if(item.price > item.oldPrice)
                            {
                                return (
                                    <Grid item xs={12} sm={12} md={12} lg={12} xl={12}>
                                        <Grid container>
                                            <Grid item xs={12} sm={12} md={12} lg={12} xl={12}>
                                                <Grid container>
                                                    <Grid item xs={3} sm={3} md={3} lg={3} xl={3}>
                                                        <Typography>{item.name}:</Typography>
                                                    </Grid>
                                                    <Grid item xs={3} sm={3} md={3} lg={3} xl={3}>
                                                        <Typography>{item.price/item.oldPrice}:</Typography>
                                                    </Grid>
                                                </Grid>
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                )
                            } else {}

                        })
                    }
                </DialogContent>
                <DialogActions>
                    <Button onClick={closeDialogBox} color="primary">
                        Disagree
                    </Button>
                    <Button onClick={closeDialogBox} color="primary" autoFocus>
                        Agree
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};
const mapStateToProps = (state) => {
    return {
        foodItems: state.FOOD_ITEM.items
    }
};

const mapDispatchToProps = (dispatch) => {
    return {

    }
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Cart);
